<script>
  import ContactUs from '@/components/ContactUs.vue'
  import fab from 'vue-fab'
  import VueScrollTo from 'vue-scrollto'
  export default {
    components: {
      ContactUs,
      fab,
      VueScrollTo
    },
    name: 'Landing',
    data () {
      return {
        title: 'Lynxmasters',
        selected: undefined,
        bgColor: '#778899',
        position: 'bottom-right',
        isActive: false,
        sticky: '',
        fabActions: [
          {
            name: 'scrollToTop',
            tooltip: 'Take me up!',
            icon: 'arrow_upward'
          },
          {
            name: 'login',
            tooltip: 'Login!',
            icon: 'account_circle'
          },
          {
            name: 'signUp',
            tooltip: 'Sign Up!',
            icon: 'person_add'
          },
          {
            name: 'contactUs',
            tooltip: 'Contact Us!',
            icon: 'mail'
          }
        ]
      }
    },
    methods:{
      scrollToTop(){
        VueScrollTo.scrollTo('#navigation', 2000);
      },
      goToLoginPage(){
        this.$router.push({name: 'Login'});
      },
      goToSignUpPage(){
        this.$router.push({name: 'Signup'});
      },
      scrollToContactUs(){
        VueScrollTo.scrollTo('#contactSection', 2000);
      },
      handleScroll(){
        this.sticky =  document.getElementById('sectionsNav').offsetTop
        let anchor = document.getElementById('scroller-anchor').offsetTop
        let anchorSpace = window.pageYOffset - anchor
        if (window.pageYOffset > this.sticky && (anchorSpace > 0)) {
          this.isActive = true;
        } else {
          this.isActive = false;
        }
      }
    },
    created () {
      window.addEventListener('scroll', this.handleScroll);
    },
    destroyed () {
      window.removeEventListener('scroll', this.handleScroll);
    }
  }
</script>

